import React, { useState } from 'react';

function TodoForm({ onSubmit }) {
  const [text, setText] = useState('');
  const [priority, setPriority] = useState('Medium');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!text.trim()) return;

    onSubmit({ text, priority });
    setText('');
    setPriority('Medium');
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col gap-4 mb-6">
      {/* Task Input */}
      <input
        type="text"
        placeholder="üìù Enter your task here..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        className="w-full px-4 py-3 rounded-xl border border-slate-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 bg-white/80 backdrop-blur"
      />

      {/* Priority Dropdown */}
      <select
        value={priority}
        onChange={(e) => setPriority(e.target.value)}
        className="w-full px-4 py-3 rounded-xl border border-slate-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 bg-white/80 backdrop-blur"
      >
        <option value="High">üî• High Priority</option>
        <option value="Medium">‚öñÔ∏è Medium Priority</option>
        <option value="Low">üå± Low Priority</option>
      </select>

      {/* Add Button */}
      <button
        type="submit"
        className="bg-blue-600 text-white px-6 py-3 rounded-xl shadow-lg hover:bg-blue-700 transition-all duration-200"
      >
        ‚ûï Add Task
      </button>
    </form>
  );
}

export default TodoForm;
