import React, { useState, useEffect } from 'react';
import TodoForm from './TodoForm';
import TodoItem from './TodoItem';
import { useNavigate } from "react-router-dom";

function TodoApp() {
  const [todos, setTodos] = useState([]);
  const navigate = useNavigate();
  const email = JSON.parse(localStorage.getItem("user"))?.email;

  // Fetch todos on load
  useEffect(() => {
    if (!email) {
      navigate("/");
      return;
    }

    fetch(`http://localhost:8080/api/todos/user/${email}`)
      .then(res => res.json())
      .then(data => setTodos(data))
      .catch(err => console.error("Error fetching todos:", err));
  }, [email, navigate]);

  // Add new todo
  const addTodo = async (task) => {
    const response = await fetch("http://localhost:8080/api/todos/add", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ...task, email }),
    });

    if (response.ok) {
      const newTodo = await response.text(); // we just re-fetch all
      fetch(`http://localhost:8080/api/todos/user/${email}`)
        .then(res => res.json())
        .then(data => setTodos(data));
    }
  };

  // Toggle completion
  const toggleComplete = async (id) => {
    await fetch(`http://localhost:8080/api/todos/toggle/${id}`, {
      method: "PUT",
    });
    setTodos(todos.map((t) =>
      t.id === id ? { ...t, completed: !t.completed } : t
    ));
  };

  // Delete todo
  const deleteTodo = async (id) => {
    await fetch(`http://localhost:8080/api/todos/delete/${id}`, {
      method: "DELETE",
    });
    setTodos(todos.filter((t) => t.id !== id));
  };

  // Edit todo
  const editTodo = async (id, updatedTask) => {
    await fetch(`http://localhost:8080/api/todos/edit/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedTask),
    });
    setTodos(todos.map((t) =>
      t.id === id ? { ...t, ...updatedTask } : t
    ));
  };

  const completedCount = todos.filter((t) => t.completed).length;
  const pendingCount = todos.length - completedCount;

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/");
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-slate-100 to-slate-200 flex flex-col items-center px-4 py-10">
      <div className="w-full max-w-xl flex justify-end mb-4">
        <button
          onClick={handleLogout}
          className="px-4 py-2 bg-red-500 text-white rounded-lg shadow-md hover:bg-red-600 transition-all duration-200"
        >
          Logout
        </button>
      </div>

      <div className="bg-white/30 backdrop-blur-lg rounded-2xl shadow-2xl w-full max-w-xl p-6 border border-slate-300">
        <h1 className="text-3xl font-semibold text-slate-800 mb-6 text-center">🚀 To Do List</h1>
        <TodoForm onSubmit={addTodo} />
        <div className="space-y-4">
          {todos.map((todo) => (
            <TodoItem
              key={todo.id}
              todo={todo}
              onToggle={toggleComplete}
              onDelete={deleteTodo}
              onEdit={editTodo}
            />
          ))}
        </div>
        <div className="mt-6 text-sm text-slate-700 flex justify-between">
          <span>📋 Total: {todos.length}</span>
          <span>✅ Completed: {completedCount}</span>
          <span>⏳ Pending: {pendingCount}</span>
        </div>
      </div>
    </div>
  );
}

export default TodoApp;
