import React, { useState, useEffect } from 'react';
import TodoForm from './TodoForm';
import TodoItem from './TodoItem';
import { useNavigate } from 'react-router-dom';

function TodoApp() {
  const [todos, setTodos] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [filter, setFilter] = useState("All");
  const [searchText, setSearchText] = useState("");
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const userEmail = user?.email;
  

  useEffect(() => {
    if (!userEmail) {
      navigate('/');
      return;
    }
    fetch(`http://localhost:8080/api/todos/${userEmail}`)
      .then((res) => res.json())
      .then((data) => setTodos(data))
      .catch((err) => {
        console.error('Failed to load todos:', err);
        alert('Failed to load todos.');
      });
  }, [userEmail, navigate]);

  const addTodo = (task) => {
    const newTodo = {
      text: task.text,
      priority: task.priority,
      dueDate: task.dueDate,
      completed: false,
    };

    fetch(`http://localhost:8080/api/todos/${userEmail}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newTodo),
    })
      .then((res) => res.text())
      .then(() => {
        setTodos([...todos, { ...newTodo, id: Date.now() }]);
      });
  };

  const toggleComplete = (id) => {
    const todo = todos.find((t) => t.id === id);
    const updated = { ...todo, completed: !todo.completed };

    fetch(`http://localhost:8080/api/todos/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updated),
    }).then(() =>
      setTodos(todos.map((t) => (t.id === id ? updated : t)))
    );
  };

  const deleteTodo = (id) => {
    fetch(`http://localhost:8080/api/todos/${id}`, {
      method: 'DELETE',
    }).then(() => setTodos(todos.filter((t) => t.id !== id)));
  };

  const editTodo = (id, updatedTask) => {
    const updated = { ...todos.find((t) => t.id === id), ...updatedTask };

    fetch(`http://localhost:8080/api/todos/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updated),
    }).then(() =>
      setTodos(todos.map((t) => (t.id === id ? updated : t)))
    );
  };

  const filteredTodos = todos.filter((todo) => {
    const matchesFilter =
      filter === "All" ||
      (filter === "Completed" && todo.completed) ||
      (filter === "Pending" && !todo.completed);
    const matchesSearch = todo.text.toLowerCase().includes(searchText.toLowerCase());
    return matchesFilter && matchesSearch;
  });

  const completedCount = todos.filter((t) => t.completed).length;
  const pendingCount = todos.length - completedCount;
  const completionPercent = todos.length > 0 ? Math.round((completedCount / todos.length) * 100) : 0;

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  useEffect(() => {
    const root = window.document.documentElement;
    if (darkMode) {
      root.classList.add('dark');
    } else {
      root.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <div className="min-h-screen bg-gradient-to-r from-slate-100 to-slate-200 dark:from-gray-900 dark:to-gray-800 text-black dark:text-white transition-colors">
      <div className="flex justify-between items-center p-4 max-w-xl mx-auto">
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="text-sm text-indigo-600 hover:underline"
        >
          {darkMode ? "☀️ Light Mode" : "🌙 Dark Mode"}
        </button>
        <button
          onClick={handleLogout}
          className="text-red-600 text-sm font-medium hover:underline"
        >
          Logout
        </button>
      </div>

      <div className="bg-white/30 backdrop-blur-lg rounded-2xl shadow-2xl w-full max-w-xl p-6 border border-slate-300 mx-auto">
        <h1 className="text-3xl font-semibold text-center text-slate-800 dark:text-white mb-6">🚀 Project Task Manager</h1>

        <TodoForm onSubmit={addTodo} />

        <div className="flex justify-between flex-wrap gap-2 mb-4 items-center">
          <input
            type="text"
            placeholder="Search tasks..."
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            className="px-4 py-2 rounded border border-gray-400 dark:bg-gray-700 w-full sm:w-auto"
          />

          <div className="flex gap-2 mt-2 sm:mt-0">
            <button onClick={() => setFilter("All")} className={`px-3 py-1 rounded ${filter === 'All' ? 'bg-indigo-600 text-white' : 'bg-gray-300 dark:bg-gray-700 text-black dark:text-white'}`}>All</button>
            <button onClick={() => setFilter("Completed")} className={`px-3 py-1 rounded ${filter === 'Completed' ? 'bg-indigo-600 text-white' : 'bg-gray-300 dark:bg-gray-700 text-black dark:text-white'}`}>Completed</button>
            <button onClick={() => setFilter("Pending")} className={`px-3 py-1 rounded ${filter === 'Pending' ? 'bg-indigo-600 text-white' : 'bg-gray-300 dark:bg-gray-700 text-black dark:text-white'}`}>Pending</button>
          </div>
        </div>

        <div className="space-y-4">
          {filteredTodos.map((todo) => (
            <TodoItem
              key={todo.id}
              todo={todo}
              onToggle={toggleComplete}
              onDelete={deleteTodo}
              onEdit={editTodo}
            />
          ))}
        </div>

        <div className="mt-6 text-sm text-slate-700 dark:text-gray-200 flex justify-between">
          <span>📋 Total: {todos.length}</span>
          <span>✅ Completed: {completedCount}</span>
          <span>⏳ Pending: {pendingCount}</span>
        </div>

        <div className="mt-4 w-full bg-gray-300 dark:bg-gray-700 h-3 rounded-full overflow-hidden">
          <div className="bg-green-500 h-full" style={{ width: `${completionPercent}%` }}></div>
        </div>
      </div>
    </div>
  );
}

export default TodoApp;
