import React, { useState } from 'react';
import TodoForm from './TodoForm';
import { Pencil, Trash2, CheckCircle, Circle } from 'lucide-react';

function TodoItem({ todo, onToggle, onDelete, onEdit }) {
  const [isEditing, setIsEditing] = useState(false);

  const handleEdit = (task) => {
    onEdit(todo.id, task);
    setIsEditing(false);
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High':
        return 'bg-red-500 text-white';
      case 'Medium':
        return 'bg-yellow-500 text-white';
      case 'Low':
        return 'bg-green-500 text-white';
      default:
        return 'bg-gray-300 text-black';
    }
  };

  return (
    <div
      className={`bg-white rounded-xl shadow-sm border border-slate-200 px-4 py-3 flex justify-between items-center transition-all duration-300 ${
        todo.completed ? 'opacity-60' : ''
      }`}
    >
      {isEditing ? (
        <div className="w-full">
          <TodoForm onSubmit={handleEdit} editTodo={todo} />
        </div>
      ) : (
        <>
          <div className="flex items-start gap-3 w-full">
            <button
              onClick={() => onToggle(todo.id)}
              className="mt-1 text-indigo-600"
              title="Mark as complete"
            >
              {todo.completed ? <CheckCircle size={20} /> : <Circle size={20} />}
            </button>
            <div className="flex-1">
              <h3 className={`text-md font-medium ${todo.completed ? 'line-through text-gray-500' : 'text-slate-800'}`}>
                {todo.text}
              </h3>
              <span className={`text-xs mt-1 inline-block px-2 py-1 rounded-full ${getPriorityColor(todo.priority)}`}>
                {todo.priority}
              </span>
            </div>
          </div>
          <div className="flex gap-2">
            <button
              onClick={() => setIsEditing(true)}
              className="text-slate-500 hover:text-indigo-600 transition-all"
              title="Edit"
            >
              <Pencil size={18} />
            </button>
            <button
              onClick={() => onDelete(todo.id)}
              className="text-slate-500 hover:text-red-600 transition-all"
              title="Delete"
            >
              <Trash2 size={18} />
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default TodoItem;
