  const [searchText, setSearchText] = useState("");
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const userEmail = user?.email;

  useEffect(() => {
    if (!userEmail) {
      navigate('/');
      return;
    }
    fetch(`http://localhost:8080/api/todos/${userEmail}`)
      .then((res) => res.json())
      .then((data) => setTodos(data))
      .catch((err) => {
        console.error('Failed to load todos:', err);
        alert('Failed to load todos.');
      });
  }, [userEmail, navigate]);

  const addTodo = (task) => {
    const newTodo = {
      text: task.text,
      priority: task.priority,
      dueDate: task.dueDate,
      completed: false,
    };

    fetch(`http://localhost:8080/api/todos/${userEmail}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newTodo),
    })
      .then((res) => res.text())
      .then(() => {
        setTodos([...todos, { ...newTodo, id: Date.now() }]);
      });
  };

  const toggleComplete = (id) => {
    const todo = todos.find((t) => t.id === id);
    const updated = { ...todo, completed: !todo.completed };

    fetch(`http://localhost:8080/api/todos/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updated),
    }).then(() =>
      setTodos(todos.map((t) => (t.id === id ? updated : t)))
    );
  };

  const deleteTodo = (id) => {
    fetch(`http://localhost:8080/api/todos/${id}`, {
      method: 'DELETE',
    }).then(() => setTodos(todos.filter((t) => t.id !== id)));
  };

  const editTodo = (id, updatedTask) => {
    const updated = { ...todos.find((t) => t.id === id), ...updatedTask };

    fetch(`http://localhost:8080/api/todos/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updated),
    }).then(() =>
      setTodos(todos.map((t) => (t.id === id ? updated : t)))
    );
  };

  const priorityValue = (p) => {
    if (p === "Low") return 1;
    if (p === "Moderate") return 2;
    return 3; // High
  };

  const filteredTodos = todos
    .filter((todo) => {
      const matchesFilter =
        filter === "All" ||
        (filter === "Completed" && todo.completed) ||
        (filter === "Pending" && !todo.completed);
      const matchesSearch = todo.text.toLowerCase().includes(searchText.toLowerCase());
      return matchesFilter && matchesSearch;
    })
    .sort((a, b) => {
      if (sortOrder === "lowToHigh") return priorityValue(a.priority) - priorityValue(b.priority);
      if (sortOrder === "highToLow") return priorityValue(b.priority) - priorityValue(a.priority);
      return 0;
    });

  const completedCount = todos.filter((t) => t.completed).length;
  const pendingCount = todos.length - completedCount;

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  useEffect(() => {
    const root = window.document.documentElement;
    if (darkMode) {
      root.classList.add('dark');
    } else {
      root.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <div className="min-h-screen bg-gradient-to-r from-slate-100 to-slate-200 dark:from-gray-900 dark:to-gray-800 text-black dark:text-white transition-colors">
      <div className="flex justify-between items-center p-4 max-w-xl mx-auto">
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="text-sm text-indigo-600 hover:underline"
        >
          {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
        </button>
        <button
          onClick={handleLogout}
          className="text-red-600 text-sm font-medium hover:underline"
        >
          Logout
        </button>
      </div>

      <div className="bg-white/30 backdrop-blur-lg rounded-2xl shadow-2xl w-full max-w-xl p-6 border border-slate-300 mx-auto">
        <h1 className="text-3xl font-semibold text-center text-slate-800 dark:text-white mb-6">üöÄ To - Do List</h1>

        <TodoForm onSubmit={addTodo} />

        <div className="flex flex-wrap gap-2 items-center justify-between mb-4">
          <input
            type="text"
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            placeholder="Search tasks..."
            className="px-3 py-2 rounded border border-gray-400 dark:bg-gray-700 dark:text-white w-full sm:w-auto"
          />

          <select
            value={sortOrder}
            onChange={(e) => setSortOrder(e.target.value)}
            className="px-2 py-2 rounded border border-gray-400 dark:bg-gray-700 dark:text-white"
          >
            <option value="none">Sort by Urgency</option>
            <option value="lowToHigh">Low ‚Üí High</option>
            <option value="highToLow">High ‚Üí Low</option>
          </select>

          <div className="flex gap-1">
            <button onClick={() => setFilter('All')} className="bg-slate-500 text-white px-3 py-1 rounded">All</button>
            <button onClick={() => setFilter('Completed')} className="bg-green-600 text-white px-3 py-1 rounded">Completed</button>
            <button onClick={() => setFilter('Pending')} className="bg-yellow-500 text-white px-3 py-1 rounded">Pending</button>
          </div>
        </div>

        <div className="space-y-4">
          {filteredTodos.map((todo) => (
            <TodoItem
              key={todo.id}
              todo={todo}
              onToggle={toggleComplete}
              onDelete={deleteTodo}
              onEdit={editTodo}
            />
          ))}
        </div>

        <div className="mt-6 text-sm text-slate-700 dark:text-gray-200 flex justify-between">
          <span>üìã Total: {todos.length}</span>
          <span>‚úÖ Completed: {completedCount}</span>
          <span>‚è≥ Pending: {pendingCount}</span>
        </div>

        <div className="mt-2 w-full bg-gray-300 dark:bg-gray-600 h-3 rounded-full overflow-hidden">
          <div className="bg-green-500 h-full" style={{ width: `${todos.length ? (completedCount / todos.length) * 100 : 0}%` }}></div>
        </div>

        <div className="flex justify-between mt-6 flex-wrap gap-2">
          <button
            onClick={() => {
              const updates = todos.map(t => ({ ...t, completed: true }));
              setTodos(updates);
              updates.forEach(todo => {
                fetch(`http://localhost:8080/api/todos/${todo.id}`, {
                  method: 'PUT',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify(todo),
                });
              });
            }}
            className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
          >
            Mark All Completed
          </button>

          <button
            onClick={() => {
              todos.forEach(todo => {
                fetch(`http://localhost:8080/api/todos/${todo.id}`, {
                  method: 'DELETE',
                });
              });
              setTodos([]);
            }}
            className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
          >
            Delete All Tasks
          </button>
        </div>
      </div>
    </div>
  );
}

export default TodoApp;
