import React, { useState, useEffect } from 'react';
import TodoForm from './TodoForm';
import TodoItem from './TodoItem';
import { useNavigate } from "react-router-dom";

function TodoApp() {
  const [todos, setTodos] = useState([]);
  const navigate = useNavigate();

  const user = JSON.parse(localStorage.getItem("user"));

  // ‚úÖ Fetch todos for the current user
  const fetchTodos = async () => {
    try {
      const res = await fetch(`http://localhost:8080/api/todos/user/${user.email}`);
      const data = await res.json();
      if (Array.isArray(data)) {
        setTodos(data);
      } else {
        setTodos([]);
      }
    } catch (err) {
      console.error("Failed to fetch todos:", err);
      setTodos([]);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  // ‚úÖ Add new todo
  const addTodo = async (task) => {
    try {
      await fetch("http://localhost:8080/api/todos/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          email: user.email,
          text: task.text,
          priority: task.priority
        }),
      });
      fetchTodos();
    } catch (err) {
      console.error("Failed to add todo:", err);
    }
  };

  // ‚úÖ Toggle completed
  const toggleComplete = async (id) => {
    try {
      await fetch(`http://localhost:8080/api/todos/toggle/${id}`, {
        method: "PUT",
      });
      fetchTodos();
    } catch (err) {
      console.error("Failed to toggle todo:", err);
    }
  };

  // ‚úÖ Delete todo
  const deleteTodo = async (id) => {
    try {
      await fetch(`http://localhost:8080/api/todos/delete/${id}`, {
        method: "DELETE",
      });
      fetchTodos();
    } catch (err) {
      console.error("Failed to delete todo:", err);
    }
  };

  // ‚úÖ Edit todo
  const editTodo = async (id, updatedTask) => {
    try {
      await fetch(`http://localhost:8080/api/todos/edit/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          text: updatedTask.text,
          priority: updatedTask.priority,
        }),
      });
      fetchTodos();
    } catch (err) {
      console.error("Failed to edit todo:", err);
    }
  };

  // ‚úÖ Logout
  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/");
  };

  const completedCount = todos.filter((t) => t.completed).length;
  const pendingCount = todos.length - completedCount;

  return (
    <div className="min-h-screen bg-gradient-to-r from-slate-100 to-slate-200 flex flex-col items-center px-4 py-10">

      {/* üîì Logout Button */}
      <div className="w-full max-w-xl flex justify-end mb-4">
        <button
          onClick={handleLogout}
          className="px-4 py-2 bg-red-500 text-white rounded-lg shadow-md hover:bg-red-600 transition-all duration-200"
        >
          Logout
        </button>
      </div>

      <div className="bg-white/30 backdrop-blur-lg rounded-2xl shadow-2xl w-full max-w-xl p-6 border border-slate-300">
        <h1 className="text-3xl font-semibold text-slate-800 mb-6 text-center">üöÄ To Do List </h1>
        <TodoForm onSubmit={addTodo} />
        <div className="space-y-4">
          {todos.map((todo) => (
            <TodoItem
              key={todo.id}
              todo={todo}
              onToggle={toggleComplete}
              onDelete={deleteTodo}
              onEdit={editTodo}
            />
          ))}
        </div>
        <div className="mt-6 text-sm text-slate-700 flex justify-between">
          <span>üìã Total: {todos.length}</span>
          <span>‚úÖ Completed: {completedCount}</span>
          <span>‚è≥ Pending: {pendingCount}</span>
        </div>
      </div>
    </div>
  );
}

export default TodoApp;
