import React, { useState } from 'react';

function TodoItem({ todo, onToggle, onDelete, onEdit }) {
  const [editing, setEditing] = useState(false);
  const [newText, setNewText] = useState(todo.text);
  const [newPriority, setNewPriority] = useState(todo.priority);
  const [newDueDate, setNewDueDate] = useState(todo.dueDate || '');

  const handleEdit = () => {
    if (!newText.trim()) return;
    onEdit(todo.id, { text: newText, priority: newPriority, dueDate: newDueDate });
    setEditing(false);
  };

  return (
    <div className={`p-4 rounded border shadow-sm ${todo.completed ? 'bg-green-100 dark:bg-green-900' : 'bg-white dark:bg-gray-700'}`}>
      {editing ? (
        <div className="space-y-2">
          <input
            value={newText}
            onChange={(e) => setNewText(e.target.value)}
            className="w-full px-2 py-1 rounded border text-black"
          />
          <div className="flex gap-2">
            <select
              value={newPriority}
              onChange={(e) => setNewPriority(e.target.value)}
              className="px-2 py-1 rounded border text-black"
            >
              <option value="Low">Low</option>
              <option value="Moderate">Moderate</option>
              <option value="High">High</option>
            </select>
            <input
              type="date"
              value={newDueDate}
              onChange={(e) => setNewDueDate(e.target.value)}
              className="px-2 py-1 rounded border text-black"
            />
            <button onClick={handleEdit} className="bg-blue-600 text-white px-2 py-1 rounded">Save</button>
          </div>
        </div>
      ) : (
        <div className="space-y-1">
          <div className="flex justify-between items-center">
            <div>
              <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => onToggle(todo.id)}
                className="mr-2"
              />
              <span className={todo.completed ? 'line-through' : ''}>{todo.text}</span>
            </div>
            <div className="flex gap-2">
              <button onClick={() => setEditing(true)} className="text-blue-600">Edit</button>
              <button onClick={() => onDelete(todo.id)} className="text-red-600">Delete</button>
            </div>
          </div>
          <div className="text-sm text-gray-500 dark:text-gray-300">
            Priority: <strong>{todo.priority}</strong>
            {todo.dueDate && (
              <> | Due: <strong>{todo.dueDate}</strong></>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default TodoItem;
