import React, { useState, useEffect } from 'react';
import TodoForm from './TodoForm';
import TodoItem from './TodoItem';
import { useNavigate } from "react-router-dom";

function TodoApp() {
  const [todos, setTodos] = useState([]);
  const [searchText, setSearchText] = useState('');
  const [filter, setFilter] = useState('all'); // all, completed, pending
  const [sortOrder, setSortOrder] = useState('none'); // none, lowToHigh, highToLow
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem('darkMode') === 'true');
  const navigate = useNavigate();

  useEffect(() => {
    localStorage.setItem('darkMode', darkMode);
    document.documentElement.classList.toggle('dark', darkMode);
  }, [darkMode]);

  const addTodo = (task) => {
    setTodos([
      ...todos,
      {
        id: Date.now(),
        text: task.text,
        priority: task.priority,
        completed: false,
      },
    ]);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/");
  };

  const toggleComplete = (id) => {
    setTodos(todos.map((t) =>
      t.id === id ? { ...t, completed: !t.completed } : t
    ));
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter((t) => t.id !== id));
  };

  const editTodo = (id, updatedTask) => {
    setTodos(todos.map((t) =>
      t.id === id ? { ...t, text: updatedTask.text, priority: updatedTask.priority } : t
    ));
  };

  const clearCompleted = () => {
    setTodos(todos.filter((t) => !t.completed));
  };

  const markAllComplete = () => {
    setTodos(todos.map((t) => ({ ...t, completed: true })));
  };

  const completedCount = todos.filter((t) => t.completed).length;
  const pendingCount = todos.length - completedCount;

  // Filtered + Searched + Sorted Tasks
  const filteredTodos = todos
    .filter((todo) =>
      todo.text.toLowerCase().includes(searchText.toLowerCase()) &&
      (filter === 'all' || (filter === 'completed' && todo.completed) || (filter === 'pending' && !todo.completed))
    )
    .sort((a, b) => {
      if (sortOrder === 'lowToHigh') return priorityValue(a.priority) - priorityValue(b.priority);
      if (sortOrder === 'highToLow') return priorityValue(b.priority) - priorityValue(a.priority);
      return 0;
    });

  const priorityValue = (p) => {
    if (p === 'Low') return 1;
    if (p === 'Moderate') return 2;
    return 3; // High
  };

  const completionPercent = todos.length > 0 ? Math.round((completedCount / todos.length) * 100) : 0;

  return (
    <div className="min-h-screen bg-gradient-to-r from-slate-100 to-slate-200 dark:from-gray-900 dark:to-gray-800 px-4 py-10 text-black dark:text-white transition-all">
      <div className="max-w-xl mx-auto space-y-6">
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-bold text-center">üìù To-Do Dashboard</h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="bg-slate-700 text-white px-3 py-1 rounded hover:bg-slate-600"
          >
            {darkMode ? '‚òÄ Light' : 'üåô Dark'}
          </button>
        </div>

        <TodoForm onSubmit={addTodo} />

        <div className="flex gap-2 flex-wrap justify-between">
          <input
            type="text"
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            placeholder="Search tasks..."
            className="px-3 py-2 rounded border w-full sm:w-auto text-black"
          />
          <div className="flex gap-1">
            <button onClick={() => setFilter('all')} className="btn">All</button>
            <button onClick={() => setFilter('completed')} className="btn">Completed</button>
            <button onClick={() => setFilter('pending')} className="btn">Pending</button>
          </div>
          <select
            value={sortOrder}
            onChange={(e) => setSortOrder(e.target.value)}
            className="px-2 py-1 rounded border text-black"
          >
            <option value="none">Sort Priority</option>
            <option value="lowToHigh">Low ‚Üí High</option>
            <option value="highToLow">High ‚Üí Low</option>
          </select>
        </div>

        <div className="space-y-4">
          {filteredTodos.map((todo) => (
            <TodoItem
              key={todo.id}
              todo={todo}
              onToggle={toggleComplete}
              onDelete={deleteTodo}
              onEdit={editTodo}
            />
          ))}
        </div>

        <div className="flex justify-between mt-4 text-sm">
          <span>üìã Total: {todos.length}</span>
          <span>‚úÖ Completed: {completedCount}</span>
          <span>‚è≥ Pending: {pendingCount}</span>
        </div>

        <div className="mt-4 w-full bg-gray-300 dark:bg-gray-600 h-3 rounded-full overflow-hidden">
          <div className="bg-green-500 h-full" style={{ width: `${completionPercent}%` }}></div>
        </div>

        <div className="flex justify-between mt-4">
          <button onClick={markAllComplete} className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700">
            Mark All Complete
          </button>
          <button onClick={clearCompleted} className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">
            Clear Completed
          </button>
          <button onClick={handleLogout} className="bg-gray-500 text-white px-3 py-1 rounded hover:bg-gray-600">
            Logout
          </button>
        </div>
      </div>
    </div>
  );
}

export default TodoApp;
