import React, { useState, useEffect } from 'react';
import TodoForm from './TodoForm';
import TodoItem from './TodoItem';
import { useNavigate } from 'react-router-dom';

function TodoApp() {
  const [todos, setTodos] = useState([]);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const userEmail = user?.email;

  // 🟡 Load todos on mount
  useEffect(() => {
    if (!userEmail) {
      navigate('/');
      return;
    }
    fetch(`http://localhost:8080/api/todos/${userEmail}`)
      .then((res) => res.json())
      .then((data) => setTodos(data))
      .catch((err) => {
        console.error('Failed to load todos:', err);
        alert('Failed to load todos.');
      });
  }, [userEmail, navigate]);

  // ✅ Add todo
  const addTodo = (task) => {
    const newTodo = {
      text: task.text,
      priority: task.priority,
      completed: false,
    };

    fetch(`http://localhost:8080/api/todos/${userEmail}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newTodo),
    })
      .then((res) => res.text())
      .then(() => {
        // Refresh list
        setTodos([...todos, { ...newTodo, id: Date.now() }]); // temp id
      });
  };

  // ✅ Toggle complete
  const toggleComplete = (id) => {
    const todo = todos.find((t) => t.id === id);
    const updated = { ...todo, completed: !todo.completed };

    fetch(`http://localhost:8080/api/todos/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updated),
    }).then(() =>
      setTodos(todos.map((t) => (t.id === id ? updated : t)))
    );
  };

  // ✅ Delete todo
  const deleteTodo = (id) => {
    fetch(`http://localhost:8080/api/todos/${id}`, {
      method: 'DELETE',
    }).then(() => setTodos(todos.filter((t) => t.id !== id)));
  };

  // ✅ Edit todo
  const editTodo = (id, updatedTask) => {
    const updated = { ...todos.find((t) => t.id === id), ...updatedTask };

    fetch(`http://localhost:8080/api/todos/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updated),
    }).then(() =>
      setTodos(todos.map((t) => (t.id === id ? updated : t)))
    );
  };

  const completedCount = todos.filter((t) => t.completed).length;
  const pendingCount = todos.length - completedCount;

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-slate-100 to-slate-200 flex flex-col items-center px-4 py-10">
      <div className="bg-white/30 backdrop-blur-lg rounded-2xl shadow-2xl w-full max-w-xl p-6 border border-slate-300">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-semibold text-slate-800 text-center w-full">🚀 Project Task Manager</h1>
          <button onClick={handleLogout} className="text-red-600 text-sm font-medium hover:underline absolute top-6 right-8">
            Logout
          </button>
        </div>
        <TodoForm onSubmit={addTodo} />
        <div className="space-y-4">
          {todos.map((todo) => (
            <TodoItem
              key={todo.id}
              todo={todo}
              onToggle={toggleComplete}
              onDelete={deleteTodo}
              onEdit={editTodo}
            />
          ))}
        </div>
        <div className="mt-6 text-sm text-slate-700 flex justify-between">
          <span>📋 Total: {todos.length}</span>
          <span>✅ Completed: {completedCount}</span>
          <span>⏳ Pending: {pendingCount}</span>
        </div>
      </div>
    </div>
  );
}

export default TodoApp;
