import React, { useState } from 'react';

function TodoForm({ onSubmit, editTodo }) {
  const [text, setText] = useState(editTodo?.text || '');
  const [priority, setPriority] = useState(editTodo?.priority || 'Medium');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (text.trim() === '') return;
    onSubmit({ text, priority });
    setText('');
    setPriority('Medium');
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
      <input
        type="text"
        placeholder="Enter task..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        required
      />
      <select value={priority} onChange={(e) => setPriority(e.target.value)}>
        <option>Low</option>
        <option>Medium</option>
        <option>High</option>
      </select>
      <button type="submit">{editTodo ? 'Update' : 'Add'}</button>
    </form>
  );
}

export default TodoForm;
