import React, { useState } from 'react';

function TodoItem({ todo, onToggle, onDelete, onEdit }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editedText, setEditedText] = useState(todo.text);
  const [editedPriority, setEditedPriority] = useState(todo.priority);

  const handleSave = () => {
    if (editedText.trim()) {
      onEdit(todo.id, { text: editedText, priority: editedPriority });
      setIsEditing(false);
    }
  };

  return (
    <div
      className={`flex justify-between items-center bg-white dark:bg-gray-800 p-4 rounded-lg shadow-md ${
        todo.completed ? 'opacity-60 line-through' : ''
      }`}
    >
      {isEditing ? (
        <div className="flex-1 mr-4 space-y-2">
          <input
            type="text"
            value={editedText}
            onChange={(e) => setEditedText(e.target.value)}
            className="w-full px-2 py-1 border rounded-md text-black dark:text-white bg-white dark:bg-gray-700"
          />
          <select
            value={editedPriority}
            onChange={(e) => setEditedPriority(e.target.value)}
            className="w-full px-2 py-1 border rounded-md"
          >
            <option value="Low">Low</option>
            <option value="Moderate">Moderate</option>
            <option value="High">High</option>
          </select>
        </div>
      ) : (
        <div className="flex-1 mr-4">
         <p className="text-lg font-medium text-gray-900 dark:text-white">
  {todo.text}
</p>
<p className="text-sm text-gray-600 dark:text-gray-300">
  Priority: {todo.priority}
</p>
<p className="text-sm text-gray-600 dark:text-gray-300">
  Due: {todo.dueDate || 'N/A'}
</p>

        </div>
      )}

      <div className="flex items-center space-x-2">
        {isEditing ? (
          <button
            onClick={handleSave}
            className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
          >
            Save
          </button>
        ) : (
          <>
            <button
              onClick={() => onToggle(todo.id)}
              className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
            >
              {todo.completed ? 'Undo' : 'Done'}
            </button>
            <button
              onClick={() => setIsEditing(true)}
              className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600"
            >
              Edit
            </button>
            <button
              onClick={() => onDelete(todo.id)}
              className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
            >
              Delete
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default TodoItem;
